import streamlit as st
import pandas as pd
import numpy as np
import time
from sklearn.feature_extraction.text import CountVectorizer
from sklearn.model_selection import train_test_split
from sklearn.naive_bayes import MultinomialNB

# functions

maindata = pd.read_csv("dataset.csv")

@st.cache
def model(language):
        data = pd.read_csv("dataset.csv")
        x = np.array(data["Text"])
        y = np.array(data["language"])
        cv = CountVectorizer()
        X = cv.fit_transform(x)
        X_train,X_test,y_train,y_test = train_test_split(X,y,test_size = 0.33,random_state = 42)
        model = MultinomialNB()
        model.fit(X_train,y_train)
        data = cv.transform([language]).toarray()
        output = model.predict(data)
        return output[0]


# elements
st.title('Language Detector Model 🚀')
st.markdown('_created by Rimmel Asghar_')
syntax =st.text_input('Enter your syntax here')
st.write('Try to write whole sentence i.e 你好你好吗')
element = st.empty()

if syntax != '':
        data = model(syntax)
        element.success(f'The entered syntax is from {data} language.')

if st.checkbox('Show supported Languages'):
    st.subheader('Supported Languages')
    st.write('This model is trained on these Languages.')
    st.table(maindata['language'].unique())

